tc = lambda t: list((complex(t[x], t[x + 1]) for x in range(0, len(t), 2)))


def getNum(string, index):
    # returns tuple
    x = ''
    while True:
        if string[index].isalpha(): break
        x += string[index]
        index += 1
    return eval(x), index


def goodData(data):
    FINAL = []
    dat = []
    x = 1
    pp1, x = getNum(data, x)
    pp2 = 0
    while True:
        if data[x] == "C":
            x += 1
            ls = getNum(data, x)
            x = ls[1]
            dat = pp1 + ls[0]
            pp2 = dat[-4:-2]
            pp1 = dat[-2:]
            FINAL.append(tc(dat))
        elif data[x] == 'S':
            x += 1
            ls = getNum(data, x)
            x = ls[1]
            dat = pp1 + (2 * pp1[0] - pp2[0], 2 * pp1[1] - pp2[1]) + ls[0]
            pp2 = dat[-4:-2]
            pp1 = dat[-2:]
            FINAL.append(tc(dat))
        elif data[x] == 'H':
            x += 1
            ls = getNum(data, x)
            x = ls[1]
            dat = pp1 + (ls[0], pp1[1])
            pp1 = dat[-2:]
            FINAL.append(tc(dat))
        elif data[x] == 'V':
            x += 1
            ls = getNum(data, x)
            x = ls[1]
            dat = pp1 + (pp1[0], ls[0])
            pp1 = dat[-2:]
            FINAL.append(tc(dat))
        elif data[x] == 'L':
            x += 1
            ls = getNum(data, x)
            x = ls[1]
            dat = pp1 + ls[0]
            pp1 = dat[-2:]
            FINAL.append(tc(dat))
        elif data[x] == 'Z':
            break
    return FINAL


# Dimensions of drawing paper
height = 297
width = 210


def C(complex_list):
    corrected = []

    for z in complex_list:
        real = z.real
        imag = z.imag

        corrected_real = real - (width / 2)
        corrected_imag = (height / 2) - imag
        corrected.append(complex(corrected_real, corrected_imag))

    p0 = corrected[0]
    p1 = corrected[1]
    p2 = corrected[2]
    p3 = corrected[3]

    t = np.linspace(0, 1, 1000)
    complex_coordinate = np.array(
        ((1 - t) ** 3) * p0 + 3 * t * ((1 - t) ** 2) * p1 + 3 * (t ** 2) * (1 - t) * p2 + (t ** 3) * p3, dtype=complex)

    return complex_coordinate


def Q(complex_list):
    corrected = []

    for z in complex_list:
        real = z.real
        imag = z.imag

        corrected_real = real - (width / 2)
        corrected_imag = (height / 2) - imag
        corrected.append(complex(corrected_real, corrected_imag))

    p0 = corrected[0]
    p1 = corrected[1]
    p2 = corrected[2]

    t = np.linspace(0, 1, 1000)
    complex_coordinate = np.array(((1 - t) ** 2) * p0 + 2 * t * (1 - t) * p1 + (t ** 2) * p2, dtype=complex)

    return complex_coordinate


def L(complex_list):
    corrected = []

    for z in complex_list:
        real = z.real
        imag = z.imag

        corrected_real = real - (width / 2)
        corrected_imag = (height / 2) - imag
        corrected.append(complex(corrected_real, corrected_imag))

    p0 = corrected[0]
    p1 = corrected[1]

    t = np.linspace(0, 1, 1000)
    complex_coordinate = np.array((1 - t) * p0 + t * p1, dtype=complex)

    return complex_coordinate


converted_array = goodData(path_string)

complex_coordinates = []

for list in converted_array:
    if len(list) == 4:
        cubic_bezier_curve_info = C(list)
        for elements in cubic_bezier_curve_info:
            complex_coordinates.append(elements)
    elif len(list) == 2:
        line_info = L(list)
        for elements in line_info:
            complex_coordinates.append(elements)
    else:
        quadratic_bezeir_curve_info = Q(list)
        for elements in quadratic_bezeir_curve_info:
            complex_coordinates.append(elements)

new_list = complex_coordinates

# for testing out the function
